mapper.select:

	select count(*) as  overcnt
	from WINGSAPP.WJQ.wjq.ZMMT2004


	
	and ebeln  = #{web_EBELN}
	and ebelp  = #{web_EBELP}

mapper.update:
UPDATE WINGSAPP.WJQ.wjq.ZMMT2004
SUPP_JUPDT = replace(#{web_SUPP_JUPDT},'.',''), SUPP_JUPTM = replace(#{web_SUPP_JUPTM},':',''), SUPP_F_IN_UNAME = #{web_SUPP_F_IN_UNAME}, SUPP_E_MOD_UNAME = #{web_SUPP_E_MOD_UNAME}, SUPP_E_MOD_ID = #{company_cd}, F_NAP_DT = replace(#{supply_req_dt},'.',''), E_NAP_DT = replace(#{supply_req_dt2},'.',''),
,STATU = '3' WHERE EBELN = #{web_EBELN} AND EBELP = #{web_EBELP}

SET XACT_ABORT ON UPDATE tb_shipping SET PROCESS_CD = 'ST_BCP', RECEIVE_DT = CONVERT(CHAR(10), getdate(), 102) WHERE shipping_key in
#{item.shipping_key}
SET XACT_ABORT OFF

update.foreach:

	SET XACT_ABORT ON
	update WINGSAPP.WJQ.wjq.ZMMT2004
	set STATU = (case when cast(FLOOR(#{item.MengeToStatu}) as int) = 0 then '6' else  '5' end)
	where EBELN =   #{item.doc_ebeln}
	AND EBELP =  #{item.doc_ebelp}
	SET XACT_ABORT OFF


	SET XACT_ABORT ON
	update WINGSAPP.WJQ.wjq.ZMMT2006 set DELVN =  '' , DELVS = ''
	where EBELN =  #{item.doc_ebeln}
	AND EBELP =  #{item.doc_ebelp}
	AND MENGE =  #{item.doc_menge}
	SET XACT_ABORT OFF
