mapper.select:

	select count(*) as  overcnt
	from WINGSAPP.WJQ.wjq.ZMMT2004


	
	and ebeln  = #{web_EBELN}
	and ebelp  = #{web_EBELP}

mapper.update:
SET XACT_ABORT ON UPDATE tb_shipping SET PROCESS_CD = 'ST_BCP', RECEIVE_DT = CONVERT(CHAR(10), getdate(), 102) WHERE shipping_key in
#{item.shipping_key}
SET XACT_ABORT OFF

update.foreach:

SET XACT_ABORT ON
	update WINGSAPP.WJQ.wjq.ZMMT2004 SET 
		STATU =  (case when  (cast(FLOOR(#{item.doc_ebeln_menge}) as int)  -  cast(FLOOR(#{item.doc_menge}) as int)) = 0 then '3' else '5' end ) 
	WHERE EBELN =   #{item.doc_ebeln}
	AND EBELP =  #{item.doc_ebelp}
SET XACT_ABORT OFF
