mapper.select:
SELECT maintb.mtm_seq /* mtm일련번호 */ , maintb.shipping_key , maintb.material_num /* 품번 */ , maintb.pre_storage_loc /* 저장위치(변경전) */ , maintb.modi_meterial_num /* 수정품번 */ , maintb.modi_qty /* 수정수량 */ , maintb.storage_loc /* 저장위치(변경후) */ , maintb.process_id /* 처리자아이디 */ , maintb.modify_id /* 수정자아이디 */ , CONVERT(CHAR(19), maintb.modify_dt, 20) as modify_dt /* 수정일자 */ , subtb1.accept_dt /* 접수일자 */ , subtb1.shipping_method as shipping_method /* 출하방법 */ , subtb1.supply_qty as supply_qty /* 납품수량 */ , subtb2.status_nm as process_nm /* 상태명 */ , subtb1.supply_dt as supply_dt /* 남품일자 */ , oritb.company_cd , saptb.company_nm , ROW_NUMBER() OVER (
maintb.mtm_seq DESC,maintb.shipping_key,
) AS rownum FROM tb_shipping_mtm maintb inner join tb_shipping subtb1 on subtb1.shipping_key=maintb.shipping_key inner join tb_orderfor oritb on subtb1.orderfor_key=oritb.orderfor_key inner join tb_common_code subtb2 on subtb2.common_cd = subtb1.process_cd inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.company_cd
and maintb.mtm_seq = #{mtm_seq}
and maintb.shipping_key = #{shipping_key}
and subtb1.supply_dt >= #{supply_dt_start}
and subtb1.supply_dt <= #{supply_dt_end}
and oritb.order_dt >= #{order_dt_start}
and oritb.order_dt <= #{order_dt_end}
OR (maintb.material_num like concat('%',#{schword},'%')) OR (maintb.modi_meterial_num like concat('%',#{schword},'%'))
maintb.mtm_seq ASC,maintb.shipping_key ASC,
OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY

	
		SELECT 
			COUNT(*) as totalcount
		FROM tb_shipping_mtm maintb
	
	
			inner join tb_shipping subtb1
				on subtb1.shipping_key=maintb.shipping_key
	
	
		
			and maintb.mtm_seq = #{mtm_seq}
		
		
			and maintb.shipping_key = #{shipping_key}
		
		
		
			and subtb1.supply_dt >= #{supply_dt_start}
			
	
		
			
			and subtb1.supply_dt <= #{supply_dt_end}
			
		
		
			
				
    				OR (maintb.material_num like concat('%',#{schword},'%'))
    				OR (maintb.modi_meterial_num like concat('%',#{schword},'%'))

SELECT maintb.mtm_seq /* mtm일련번호 */ , maintb.shipping_key , maintb.material_num /* 품번 */ , maintb.pre_storage_loc /* 저장위치(변경전) */ , maintb.modi_meterial_num /* 수정품번 */ , maintb.modi_qty /* 수정수량 */ , maintb.storage_loc /* 저장위치(변경후) */ , maintb.process_id /* 처리자아이디 */ , maintb.modify_id /* 수정자아이디 */ , subtb1.accept_dt as order_dt /* 접수일자 */ , subtb1.shipping_method as shipping_method /* 출하방법 */ , subtb1.supply_qty as supply_qty /* 납품수량 */ , subtb2.status_nm as process_nm /* 상태명 */ , subtb1.supply_dt as supply_dt /* 남품일자 */ , oritb.company_cd , saptb.company_nm , ROW_NUMBER() OVER (
maintb.mtm_seq DESC,maintb.shipping_key,
) AS rownum FROM tb_shipping_mtm maintb
inner join tb_shipping subtb1 on subtb1.shipping_key=maintb.shipping_key and subtb1.shipping_seq > 0 and subtb1.process_cd = 'ST_CFM' inner join tb_orderfor oritb on subtb1.orderfor_key=oritb.orderfor_key
inner join tb_common_code subtb2 on subtb2.common_cd = subtb1.process_cd inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.company_cd
and maintb.shipping_key = #{shipping_key}
and subtb1.orderfor_key = #{orderfor_key}
and subtb1.supply_dt >= #{supply_dt_start}
and subtb1.supply_dt <= #{supply_dt_end}
and maintb.material_num = #{material_num}
and maintb.shipping_seq = #{shipping_seq}
and subtb1.process_cd = #{process_cd}
OR (maintb.material_num like concat('%',#{schword},'%')) OR (maintb.modi_meterial_num like concat('%',#{schword},'%'))
maintb.mtm_seq ASC,maintb.shipping_key ASC,
OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY

		
			SELECT
				COUNT(*) as totalcount
			FROM tb_shipping_mtm maintb
		
		
				inner join tb_shipping subtb1
			on subtb1.shipping_key=maintb.shipping_key
			and subtb1.shipping_seq > 0
			and subtb1.process_cd = 'ST_CFM'
		
		
		
		
			inner join tb_common_code subtb2 on subtb2.common_cd = subtb1.process_cd

		
		
			
				and maintb.shipping_key = #{shipping_key}
			
			
