SELECT maintb.mtm_seq /* mtm일련번호 */ , maintb.shipping_key /* 업체코드 */ , maintb.material_num , maintb.pre_storage_loc /* 저장위치(변경전) */ , maintb.modi_meterial_num /* 수정품번 */ , maintb.modi_qty /* 수정수량 */ , maintb.storage_loc /* 저장위치(변경후) */ , maintb.process_id /* 처리자아이디 */ , maintb.modify_id /* 수정자아이디 */ , CONVERT(CHAR(19), maintb.modify_dt, 20) as modify_dt /* 수정일자 */ , subtb1.accept_dt as accept_dt /* 발주일자 */ , subtb1.shipping_method as shipping_method /* 출하방법 */ , subtb1.supply_qty as supply_qty /* 납품수량 */ , subtb1.remain_qty as remain_qty /* 잔여수량 */ , subtb1.supply_dt as supply_dt /* 남품일자 */ FROM tb_shipping_mtm maintb
inner join tb_shipping subtb1 on subtb1.shipping_key=maintb.shipping_key
and maintb.mtm_seq = #{mtm_seq}


	SELECT count(mtm_seq) as overcnt
	FROM tb_shipping_mtm
	where maintb.shipping_key = #{shipping_key}

mapper.insert:
MERGE INTO tb_shipping_mtm AS tar USING (VALUES
( #{shipping_key}, #{material_num}, #{item.pre_storage_loc}, #{item.modi_meterial_num}, #{item.modi_qty}, #{item.storage_loc}, #{process_id}, #{modify_id}, getdate() )
) AS src ( shipping_key, material_num, pre_storage_loc, modi_meterial_num, modi_qty, storage_loc, process_id, modify_id, modify_dt ) ON tar.shipping_key = src.shipping_key WHEN NOT MATCHED BY TARGET THEN INSERT ( shipping_key, material_num, pre_storage_loc, modi_meterial_num, modi_qty, storage_loc, process_id, modify_id, modify_dt ) values ( src.shipping_key, src.material_num, src.pre_storage_loc, src.modi_meterial_num, src.modi_qty, src.storage_loc, src.process_id, src.modify_id, src.modify_dt );
