SELECT tot.orderfor_key ,tot.shipping_key ,tot.company_cd ,tot.company_nm ,tot.material_num ,tot.process_nm ,tot.supply_place ,tot.confirm_qty ,tot.shipping_method ,tot1.COMMON_CD as packing_cd ,tot1.STATUS_NM as packing_nm ,tot.packing_flag ,tot.supply_req_qty ,tot.supply_qty ,tot.supply_dt ,tot.bstrf ,tot.mtart_nm ,tot.order_dt ,packing_flag_nm , case when DENSE_RANK() OVER(order by tot.supply_dt ASC, tot.company_nm DESC, tot.supply_place DESC, tot.material_num DESC) % 2 = 0 then 'FEFFE8' else 'FFFFFF' end as trbgcolor FROM( select oritb.orderfor_key ,maintb.shipping_key , oritb.company_cd /* 업체코드 */ , saptb.company_nm company_nm /*업체명*/ , oritb.material_num material_num /* 품번 */ , subtb2.status_nm as process_nm /* 상태명 */ , subtb3.place_nm as supply_place /*납품처*/ , oritb.confirm_qty as confirm_qty /* 확정수량 */ , subtb1.status_seq as packing_seq /* 상태코드 */ , maintb.shipping_method as shipping_method /* 출하방법 */ , maintb.packing_flag , oritb.supply_req_qty as supply_req_qty /* 납품요청수량 */ , maintb.supply_qty as supply_qty /* 납품수량 */ , maintb.supply_dt /* 남품일자 */ , saptb2.BSTRF as bstrf /*포장단위*/ , case saptb3.MTART when 'ROH' then N'원자재' when 'HAWA' then N'상품' when 'FERT' then N'제품' when 'HALB' then N'반제품' end mtart_nm ,oritb.ORDER_DT as order_dt , case when maintb.packing_flag = 'Y' then N'포장' else '' end packing_flag_nm
from tb_orderfor oritb inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.user_id inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key inner join tb_common_code subtb2 on subtb2.common_cd = maintb.process_cd left outer join tb_common_code subtb1 on subtb1.common_cd = maintb.packing_cd left outer join ERP_DL.WJP.wjp.MARC saptb2 on oritb.material_num = saptb2.MATNR COLLATE Korean_Wansung_CI_AS left outer join ERP_DL.WJP.wjp.MARA saptb3 on oritb.material_num = saptb3.MATNR COLLATE Korean_Wansung_CI_AS left outer join tb_place subtb3 on subtb3.place_key = oritb.place_key WHERE maintb.use_fl ! = 'END' and maintb.SHIPPING_SEQ > 0
and oritb.company_cd = #{coperation_cd}
and maintb.supply_dt >= #{supply_dt_start}
and maintb.supply_dt <= #{supply_dt_end}
and subtb2.common_cd in('ST_SPG','ST_BCP')
and subtb2.common_cd = #{common_cd}
and oritb.material_num = #{material_list}
OR (oritb.material_num like concat('%',#{schword},'%')) OR (saptb.company_nm like concat('%',#{schword},'%'))
) tot inner join tb_common_code tot1 on tot.packing_seq = tot1.STATUS_SEQ and tot1.DIVISION_CD = 'OM'
order by tot.supply_dt DESC, tot.company_nm asc, tot.supply_place asc, tot.material_num asc
OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY

