from tb_orderfor oritb inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.user_id inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key inner join tb_common_code subtb2 on subtb2.common_cd = maintb.process_cd left outer join tb_common_code subtb1 on subtb1.common_cd = maintb.packing_cd left outer join ERP_DL.WJP.wjp.MARC saptb2 on oritb.material_num = saptb2.MATNR COLLATE Korean_Wansung_CI_AS left outer join ERP_DL.WJP.wjp.MARA saptb3 on oritb.material_num = saptb3.MATNR COLLATE Korean_Wansung_CI_AS left outer join tb_place subtb3 on subtb3.place_key = oritb.place_key WHERE maintb.use_fl ! = 'END' and maintb.SHIPPING_SEQ > 0
and oritb.company_cd = #{coperation_cd}
and maintb.supply_dt >= #{supply_dt_start}
and maintb.supply_dt <= #{supply_dt_end}
and subtb2.common_cd in('ST_SPG','ST_BCP')
and subtb2.common_cd = #{common_cd}
and oritb.material_num = #{material_list}
OR (oritb.material_num like concat('%',#{schword},'%')) OR (saptb.company_nm like concat('%',#{schword},'%'))
) tot inner join tb_common_code tot1 on tot.packing_seq = tot1.STATUS_SEQ and tot1.DIVISION_CD = 'OM' inner join ( SELECT ORDERS.CUSTOMER_CD , ORDERS.DELIV_REQ_DT , DETAIL.MATL_CD MATERIALCODE , FORMAT(SUM(DETAIL.QTY),'#.#') QUANTITY , ORITB.ORDERFOR_KEY FROM [192.9.200.159].[IMES].[dbo].WSLO_DELIV_ORDER ORDERS INNER JOIN [192.9.200.159].[IMES].[dbo].WSLO_DELIV_ORDER_DETAIL DETAIL ON ORDERS.DELIV_ORDER = DETAIL.DELIV_ORDER inner join [dbo].[TB_ORDERFOR] ORITB on ORITB.COMPANY_CD COLLATE Korean_Wansung_CI_AS = ORDERS.CUSTOMER_CD AND DETAIL.MATL_CD COLLATE Korean_Wansung_CI_AS = ORITB.MATERIAL_NUM inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key and ORDERS.DELIV_REQ_DT COLLATE Korean_Wansung_CI_AS = replace(maintb.SUPPLY_DT,'.','') where ORDERS.DELIV_ITEM_CATE = 'ZTAM' and ORDERS.STATE = 'Shipped' and ORDERS.DELIV_REQ_DT between replace(#{supply_dt_start},'.','') and replace(#{supply_dt_end},'.','') and ORITB.PROCESS_CD != 'ST_BCP' group by ORDERS.CUSTOMER_CD,DETAIL.MATL_CD , ORDERS.DELIV_REQ_DT,ORITB.ORDERFOR_KEY ) sap on tot.ORDERFOR_KEY = sap.ORDERFOR_KEY order by tot.supply_dt DESC, tot.company_nm asc, tot.material_num asc, tot.supply_place asc
OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY

select COUNT(*) as totalcount FROM( select oritb.orderfor_key ,maintb.shipping_key , oritb.company_cd /* 업체코드 */ , saptb.company_nm company_nm /*업체명*/ , oritb.material_num material_num /* 품번 */ , subtb2.status_nm as process_nm /* 상태명 */ , subtb3.place_nm as supply_place /*납품처*/ , oritb.confirm_qty as confirm_qty /* 확정수량 */ , subtb1.status_seq as packing_seq /* 상태코드 */ , maintb.shipping_method as shipping_method /* 출하방법 */ , oritb.supply_req_qty as supply_req_qty /* 납품요청수량 */ , maintb.supply_qty as supply_qty /* 납품수량 */ , maintb.supply_dt /* 남품일자 */ , saptb2.BSTRF as bstrf /*포장단위*/ , case saptb3.MTART when 'ROH' then N'원자재' when 'HAWA' then N'상품' when 'FERT' then N'제품' when 'HALB' then N'반제품' end mtart_nm ,oritb.ORDER_DT as order_dt
from tb_orderfor oritb inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.user_id inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key inner join tb_common_code subtb2 on subtb2.common_cd = maintb.process_cd left outer join tb_common_code subtb1 on subtb1.common_cd = maintb.packing_cd left outer join ERP_DL.WJP.wjp.MARC saptb2 on oritb.material_num = saptb2.MATNR COLLATE Korean_Wansung_CI_AS left outer join ERP_DL.WJP.wjp.MARA saptb3 on oritb.material_num = saptb3.MATNR COLLATE Korean_Wansung_CI_AS left outer join tb_place subtb3 on subtb3.place_key = oritb.place_key
WHERE maintb.use_fl ! = 'END' and maintb.SHIPPING_SEQ > 0
and oritb.company_cd = #{coperation_cd}
and maintb.supply_dt >= #{supply_dt_start}
and maintb.supply_dt <= #{supply_dt_end}
and oritb.material_num = #{material_list}
OR (oritb.material_num like concat('%',#{schword},'%')) OR (saptb.company_nm like concat('%',#{schword},'%'))
) tot

select distinct maintb.supply_dt /* 남품일자 */ , saptb.company_nm company_nm /*업체명*/ , subtb3.place_nm as supply_place /*납품처*/ , subtb3.place_addr
from tb_orderfor oritb inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.user_id inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key inner join tb_common_code subtb2 on subtb2.common_cd = maintb.process_cd left outer join tb_place subtb3 on subtb3.place_key = oritb.place_key WHERE maintb.use_fl ! = 'END' and maintb.SHIPPING_SEQ > 0
and oritb.company_cd = #{coperation_cd}
and maintb.supply_dt >= #{supply_dt_start}
and maintb.supply_dt <= #{supply_dt_end}
and subtb2.common_cd in('ST_SPG','ST_BCP')
and subtb2.common_cd = #{common_cd}
and oritb.material_num = #{material_list}
OR (oritb.material_num like concat('%',#{schword},'%')) OR (saptb.company_nm like concat('%',#{schword},'%'))
order by maintb.supply_dt DESC, saptb.company_nm asc, subtb3.place_nm asc

select oritb.material_num , saptb3.MTART as mtart /*자재유형*/ , saptb2.BSTRF as bstrf /*포장단위*/ , oritb.sum_qty , ISNULL(saptb4.INVEN_USE_QTY,0) as inven_use_qty , ISNULL(saptb4.INVEN_USE_QTY,0) - ISNULL(oritb.sum_qty,0) as verification_qty , isnull(mat.mtm_use_qty,0) as mtm_use_qty , isnull(yma.prod_order_qty,0) as prod_order_qty
from ( select a.material_num as material_num /* 품번 */ , SUM(case when a.confirm_qty = 0 then a.supply_req_qty else a.confirm_qty end) - ISNULL(SUM(supply_qty),0) as sum_qty from tb_orderfor a left outer join( SELECT orderfor_key, SUM(supply_qty) as supply_qty from tb_shipping where shipping_seq > 0 and process_cd in('ST_BCP','ST_SPG','ST_CFM') group by orderfor_key ) b on a.orderfor_key = b.orderfor_key where process_cd in('ST_BOS','ST_CFM','ST_OFR') group by material_num ) oritb left outer join ( SELECT MATL_CD, SUM(PLAN_QTY-GOODISH_QTY) as PROD_ORDER_QTY FROM [192.9.200.159].[IMES].[dbo].[V_MARKET_ALIVE] A GROUP BY MATL_CD ) db159 on oritb.material_num = db159.MATL_CD COLLATE Korean_Wansung_CI_AS left outer join ERP_DL.WJP.wjp.MARC saptb2 on oritb.material_num = saptb2.MATNR COLLATE Korean_Wansung_CI_AS left outer join ERP_DL.WJP.wjp.MARA saptb3 on oritb.material_num = saptb3.MATNR COLLATE Korean_Wansung_CI_AS left outer join (select b.MTART,a.MATNR, SUM(a.LABST) as INVEN_USE_QTY from ERP_DL.WJP.wjp.MARD a inner join ERP_DL.WJP.wjp.MARA b on a.MATNR = b.MATNR where a.LABST > 0 group by a.MATNR, b.MTART ) saptb4 on oritb.material_num = saptb4.MATNR COLLATE Korean_Wansung_CI_AS left outer join ( SELECT MATERIAL_NUM,ISNULL(MAX(mtm_use_qty),0) as mtm_use_qty from( select a.MATERIAL_NUM, ( select case when bb.MTART = 'HAWA' then 0 else SUM(aa.LABST) end mtm_qty from ERP_DL.WJP.wjp.MARD aa inner join ERP_DL.WJP.wjp.MARA bb on aa.MATNR = bb.MATNR where bb.MTART = 'FERT' and aa.LABST > 0 and LEFT(aa.MATNR,LEN(aa.MATNR)-2) collate SQL_Latin1_General_CP1_CI_AS = LEFT(a.MATERIAL_NUM,LEN(a.MATERIAL_NUM)-2) and aa.MATNR collate SQL_Latin1_General_CP1_CI_AS != a.MATERIAL_NUM group by bb.MTART ) mtm_use_qty from tb_orderfor a
) tot group by MATERIAL_NUM ) mat on oritb.material_num = mat.MATERIAL_NUM left outer join ( SELECT MATL_CD, ISNULL(SUM(PLAN_QTY-GOODISH_QTY),0) as prod_order_qty FROM [192.9.200.159].[IMES].[dbo].[V_MARKET_ALIVE] A group by MATL_CD ) yma on oritb.material_num = yma.MATL_CD collate Korean_Wansung_CI_AS
and oritb.material_num = #{material_list}
order by ISNULL(saptb4.INVEN_USE_QTY,0) - ISNULL(oritb.sum_qty,0) DESC, oritb.material_num asc


