mapper.select:

		SELECT
			maintb.*,saptb.company_nm
		FROM tb_orderfor maintb
		inner join OPENQUERY(ERP_DL, '
			select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a
			inner join wjp.KNA1 b on a.ID = b.KUNNR
		') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = maintb.user_id



			and maintb.company_cd = #{company_cd}



	    		and maintb.order_dt >= #{order_dt_start}




	    		and maintb.order_dt <= #{order_dt_end}



			and maintb.material_num = #{material_num}


			and maintb.knumh = #{knumh}



				and maintb.process_cd = #{process_cd}


				and maintb.process_cd not in('ST_BCP','ST_SPG')



			and maintb.use_fl = #{use_fl}


			and maintb.place_key = #{place_cd}




    				OR (maintb.material_num like concat('%',#{schword},'%'))
    				OR (maintb.supply_place like concat('%',#{schword},'%'))






		maintb.company_cd  ASC,maintb.order_dt  DESC,maintb.material_num  ASC, maintb.modify_dt ASC,




    			OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY


		SELECT 
			COUNT(*) as totalcount
		FROM tb_orderfor maintb


			inner join tb_shipping subtb3 on maintb.orderfor_key = subtb3.orderfor_key
				and subtb3.shipping_seq = 0


			inner join tb_common_code subtb1


		and subtb1.common_cd=subtb3.process_cd


			inner join tb_user_info subtb2


		and subtb2.user_id=maintb.user_id




			and maintb.company_cd = #{company_cd}



	    		and maintb.order_dt >= #{order_dt_start}




	    		and maintb.order_dt <= #{order_dt_end}



			and maintb.material_num = #{material_num}


			and maintb.knumh = #{knumh}



				and maintb.process_cd = #{process_cd}


				and maintb.process_cd not in('ST_BCP','ST_SPG')



			and maintb.use_fl = #{use_fl}


			and maintb.place_key = #{place_cd}




    				OR (maintb.material_num like concat('%',#{schword},'%'))
    				OR (maintb.supply_place like concat('%',#{schword},'%'))

SELECT maintb.company_cd /* 업체코드 */ , maintb.order_dt /* 발주일자 */ , maintb.material_num /* 품번 */ , maintb.knumh /* CondReqNo */ , maintb.user_id /* 사용자아이디 */ , maintb.supply_place /* 납품처 */ , maintb.supply_req_qty /* 납품요청수량 */ , maintb.supply_req_dt /* 납품요청일 */ , maintb.confirm_qty /* 확정수량 */ , maintb.shipping_dt /* 출하일자 */ , maintb.unit_price /* 단가 */ , maintb.total_price /* 금액 */ , maintb.last_receive_dt /* 최종수령일 */ , maintb.use_fl /* 취소여부 */ , maintb.modify_id /* 수정자아이디 */ , CONVERT(CHAR(19), maintb.modify_dt, 20) as modify_dt /* 수정일자 */ , subtb1.process_cd , subtb1.status_cd as processsub_cd /* 상태코드 */ , subtb1.status_nm as process_nm /* 상태명 */ , subtb3.shipping_seq /* 출하순번 */ , subtb3.supply_qty /* 납품수량 */ , subtb3.supply_dt /* 남품일자 */ , subtb3.receive_dt /* 수령일자 */ , subtb3.remain_qty /* 잔여수량 */ , case when subtb3.shipping_seq = 0 then 'FFFFFF' else 'e7fdb5' end trbgcolor , ROW_NUMBER() OVER (
maintb.company_cd DESC,maintb.order_dt DESC,maintb.material_num DESC,maintb.modify_dt ASC,
) AS rownum FROM tb_orderfor maintb
inner join tb_shipping subtb3 on maintb.orderfor_key = subtb3.orderfor_key and subtb3.shipping_seq > 0
inner join tb_common_code subtb1
and subtb1.common_cd=subtb3.process_cd
inner join tb_user_info subtb2
and subtb2.user_id=maintb.user_id
and subtb3.process_cd in('ST_SPG','ST_BCP')
and maintb.company_cd = #{company_cd}
and subtb3.supply_dt >= #{supply_dt_start}
and subtb3.supply_dt <= #{supply_dt_end}
and maintb.material_num = #{material_num}
and maintb.knumh = #{knumh}
OR (maintb.material_num like concat('%',#{schword},'%')) OR (maintb.supply_place like concat('%',#{schword},'%'))
maintb.company_cd ASC,maintb.order_dt ASC,maintb.material_num ASC,subtb3.shipping_seq ASC, maintb.modify_dt ASC,
OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY

SELECT COUNT(*) as totalcount FROM tb_orderfor maintb
inner join tb_shipping subtb3 on maintb.orderfor_key = subtb3.orderfor_key and subtb3.shipping_seq > 0
inner join tb_common_code subtb1
and subtb1.common_cd=subtb3.process_cd
inner join tb_user_info subtb2
and subtb2.user_id=maintb.user_id
and subtb3.process_cd in('ST_SPG','ST_BCP')
and maintb.company_cd = #{company_cd}
and subtb3.supply_dt >= #{supply_dt_start}
and subtb3.supply_dt <= #{supply_dt_end}
and maintb.material_num = #{material_num}
and maintb.knumh = #{knumh}
OR (maintb.material_num like concat('%',#{schword},'%')) OR (maintb.supply_place like concat('%',#{schword},'%'))


SELECT count(company_cd) as overcnt
FROM tb_orderfor



and company_cd = #{company_cd}
and order_dt = #{order_dt}
and material_num = #{material_num}
and place_key = #{place_key}


	and orderfor_key != #{orderfor_key}

mapper.update:
UPDATE tb_orderfor
supply_place = #{supply_place}, supply_req_qty = replace(#{supply_req_qty},',',''), supply_req_dt = #{supply_req_dt}, confirm_qty = replace(#{confirm_qty},',',''), shipping_dt = #{shipping_dt}, unit_price = replace(#{unit_price},',',''), total_price = replace(#{total_price},',',''), last_receive_dt = #{last_receive_dt}, use_fl = #{use_fl}, modify_id = #{modify_id},
,modify_dt = getdate() WHERE orderfor_key = #{orderfor_key}


UPDATE tb_shipping SET PROCESS_CD = 'ST_BCP', RECEIVE_DT = CONVERT(CHAR(10), getdate(), 102)
WHERE shipping_key in


#{item.shipping_key}

mapper.select:
select oritb.orderfor_key , maintb.shipping_key , maintb.supply_dt /* 남품일자 */ , oritb.company_cd /* 업체코드 */ , saptb.company_nm company_nm /*업체명*/ , oritb.material_num material_num /* 품번 */ , maintb.supply_qty as supply_qty /* 납품수량 */ , oritb.unit_price , oritb.unit_price * maintb.supply_qty as total_price
from tb_orderfor oritb inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.user_id inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key WHERE maintb.shipping_seq > 0 and oritb.company_cd = #{company_cd} and maintb.process_cd = #{process_cd} and left(maintb.supply_dt,7) = #{supply_month}
and oritb.material_num = #{material_list}
OR (oritb.material_num like concat('%',#{schword},'%')) OR (saptb.company_nm like concat('%',#{schword},'%'))
order by maintb.supply_dt DESC, saptb.company_nm asc, oritb.material_num asc

select maintb.supply_dt /* 남품일자 */ , oritb.company_cd /* 업체코드 */ , saptb.company_nm /* 업체코드 */ , plc.place_key , plc.place_nm /*업체명*/ , SUM(maintb.supply_qty) as supply_qty /* 납품수량 */ from tb_orderfor oritb inner join OPENQUERY(ERP_DL, ' select distinct a.ID,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR ') saptb on saptb.ID COLLATE Korean_Wansung_CI_AS = oritb.user_id inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key inner join tb_place plc on oritb.place_key = plc.place_key WHERE maintb.shipping_seq > 0 and oritb.company_cd = #{company_cd} and maintb.process_cd = #{process_cd} and left(maintb.supply_dt,7) = #{supply_month}
and oritb.material_num = #{material_list}
OR (oritb.material_num like concat('%',#{schword},'%')) OR (saptb.company_nm like concat('%',#{schword},'%'))
group by maintb.supply_dt , oritb.company_cd , saptb.company_nm , plc.place_nm , plc.place_key order by maintb.supply_dt DESC, saptb.company_nm asc


			SELECT distinct oritb.material_num
  			FROM market.dbo.tb_orderfor oritb inner join tb_shipping maintb on oritb.orderfor_key = maintb.orderfor_key
  			WHERE maintb.shipping_seq > 0 and oritb.company_cd = #{company_cd}
  				and maintb.process_cd = #{process_cd} and left(maintb.supply_dt,7) = #{supply_month}
			ORDER BY oritb.material_num ASC
