mapper.select:
SELECT maintb.user_id /* 사용자아이디 */ , maintb.auth_cd /* 권한코드 */ , maintb.sessionkey /* 세션키 */ , maintb.sessionlimit /* 세션보존시간 */ , maintb.del_fl /* 삭제여부 */ , CONVERT(CHAR(19), maintb.lastjoin_dt, 20) as lastjoin_dt /* 최종접속일자 */ , subtb1.auth_nm as subtb1_auth_nm /* 권한명 */ , subtb2.company_cd /* 업체코드 */ , subtb2.company_nm /* 업체명 */ , ROW_NUMBER() OVER (
maintb.user_id DESC,
) AS rownum FROM tb_user_info maintb inner join tb_authority subtb1 on subtb1.auth_cd=maintb.auth_cd inner join OPENQUERY(ERP_DL, ' select * from ( select a.ID as user_id ,a.ID as company_cd ,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR where isnull(a.WERKS, '''') = '''' union all SELECT ''woojin'',''woojin@1'',N''우진'' ) totuser ') subtb2 on maintb.user_id = subtb2.user_id COLLATE Korean_Wansung_CI_AS
and maintb.auth_cd = #{auth_cd}
and maintb.del_fl = #{del_fl}
and subtb2.company_cd = #{coperation_cd}
OR (subtb2.company_nm like concat('%',#{schword},'%'))
maintb.user_id ASC,
OFFSET #{nStartRecord} ROWS FETCH NEXT #{pageRecordCount} ROWS ONLY

SELECT COUNT(*) as totalcount FROM tb_user_info maintb inner join tb_authority subtb1 on subtb1.auth_cd=maintb.auth_cd inner join OPENQUERY(ERP_DL, ' select * from ( select a.ID as user_id ,a.ID as company_cd ,b.NAME1 as company_nm from wjp.ZMMT2010 a inner join wjp.KNA1 b on a.ID = b.KUNNR where isnull(a.WERKS, '''') = '''' union all SELECT ''woojin'',''woojin@1'',N''우진'' ) totuser ') subtb2 on maintb.user_id = subtb2.user_id COLLATE Korean_Wansung_CI_AS
and maintb.auth_cd = #{auth_cd}
and maintb.del_fl = #{del_fl}
and subtb2.company_cd = #{coperation_cd}
OR (subtb2.company_nm like concat('%',#{schword},'%'))

SELECT maintb.user_id /* 사용자아이디 */ , maintb.auth_cd /* 권한코드 */ , maintb.sessionkey /* 세션키 */ , maintb.sessionlimit /* 세션보존시간 */ , maintb.del_fl /* 삭제여부 */ , CONVERT(CHAR(19), maintb.lastjoin_dt, 20) as lastjoin_dt /* 최종접속일자 */ , subtb1.auth_nm as subtb1_auth_nm /* 권한명 */ FROM tb_user_info maintb inner join tb_authority subtb1 on subtb1.auth_cd=maintb.auth_cd
and maintb.auth_cd = #{auth_cd}
and maintb.del_fl = #{del_fl}


SELECT count(user_id) as overcnt
FROM tb_user_info


and user_id = #{user_id}

mapper.insert:
MERGE INTO tb_user_info AS tar USING (VALUES
( #{item.user_id}, /* 사용자아이디 */ #{item.auth_cd}, /* 권한코드 */ #{item.sessionkey}, /* 세션키 */ #{item.sessionlimit}, /* 세션보존시간 */ #{item.del_fl}, /* 삭제여부 */ getdate() )
> AS src ( user_id, auth_cd, sessionkey, sessionlimit, del_fl, lastjoin_dt ) ON tar.user_id = src.user_id WHEN MATCHED THEN UPDATE SET tar.auth_cd = src.auth_cd, tar.del_fl = src.del_fl, tar.lastjoin_dt = getdate() WHEN NOT MATCHED BY TARGET THEN INSERT ( user_id, auth_cd, sessionkey, sessionlimit, del_fl, lastjoin_dt ) values ( src.user_id src.auth_cd src.sessionkey src.sessionlimit src.del_fl getdate() );

mapper.delete:

DELETE FROM tb_user_info
WHERE user_id in 


#{item.user_id}
